import React, { useState } from 'react';
import { X, Users, ChevronLeft, ChevronRight } from 'lucide-react';
import MyRoster from './MyRoster';
import clsx from 'clsx';

const RosterSidebar = ({ 
  isOpen, 
  onToggle, 
  leagueId, 
  draftId, 
  username, 
  lastUpdated,
  isDynastyKeeper 
}) => {
  console.log('RosterSidebar: Received leagueId:', leagueId, 'isDynastyKeeper:', isDynastyKeeper);

  return (
    <>
      {/* Overlay for mobile */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={onToggle}
        />
      )}

      {/* Sidebar */}
      <div className={clsx(
        "fixed left-0 top-0 h-full bg-white shadow-lg transform transition-transform duration-300 ease-in-out z-50",
        "w-80 xl:w-96",
        isOpen ? "translate-x-0" : "-translate-x-full"
      )}>
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gray-50">
          <div className="flex items-center space-x-2">
            <Users className="w-5 h-5 text-gray-600" />
            <h2 className="text-lg font-semibold text-gray-900">My Roster</h2>
          </div>
          <button
            onClick={onToggle}
            className="p-1 rounded-md hover:bg-gray-200 transition-colors"
            title="Close roster"
          >
            <X className="w-5 h-5 text-gray-500" />
          </button>
        </div>

        {/* Content */}
        <div className="h-full overflow-y-auto pb-16">
          {isDynastyKeeper && leagueId && username ? (
            <MyRoster 
              leagueId={leagueId}
              draftId={draftId}
              username={username}
              lastUpdated={lastUpdated}
              isVisible={isOpen}
              isSidebar={true}
            />
          ) : (
            <div className="p-4 text-center text-gray-500">
              <Users className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>Roster only available for Dynasty/Keeper leagues</p>
            </div>
          )}
        </div>
      </div>

      {/* Toggle button when closed */}
      {!isOpen && (
        <button
          onClick={onToggle}
          className={clsx(
            "fixed left-0 top-1/2 transform -translate-y-1/2 z-40",
            "bg-white shadow-lg rounded-r-lg p-2 hover:bg-gray-50 transition-colors",
            "border border-l-0 border-gray-200"
          )}
          title="Show roster"
        >
          <ChevronRight className="w-5 h-5 text-gray-600" />
        </button>
      )}
    </>
  );
};

export default RosterSidebar;
