import React from 'react';
import { Wifi, WifiOff } from 'lucide-react';

const ConnectionStatus = ({ error, onRetry }) => {
  if (error) {
    return (
      <div className="bg-red-50 border-l-4 border-red-400 p-4">
        <div className="flex items-center">
          <WifiOff className="w-5 h-5 text-red-400 mr-2" />
          <p className="text-sm text-red-700">
            Connection issues detected. Data may be outdated. 
            <button 
              onClick={onRetry}
              className="ml-2 underline hover:no-underline"
            >
              Retry
            </button>
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-green-50 border-l-4 border-green-400 p-2">
      <div className="flex items-center">
        <Wifi className="w-4 h-4 text-green-400 mr-2" />
        <p className="text-xs text-green-700">Live updates active</p>
      </div>
    </div>
  );
};

export default ConnectionStatus;
